<script>
    import { onMount } from 'svelte';
    import jsPDF from 'jspdf';

    let productos = [
        { id: 1, nombre: "Televisor 50\"", precio: 50, img: "data:image/jpeg;base64," },
        { id: 2, nombre: "Lavadora", precio: 30, img: "data:image/jpeg;base64," },
        { id: 3, nombre: "Refrigerador", precio: 40, img: "https://th.bing.com/th/id/OIP.RFM4ML4AoBzX0B6Uz0UzbgHaF7?w=223&h=180&c=7&r=0&o=5&dpr=1.3&pid=1.7" },
        { id: 4, nombre: "Horno Microondas", precio: 20, img: "https://cdn1.coppel.com/images/catalog/pm/6505953-1.jpg" },
        { id: 5, nombre: "Aire Acondicionado", precio: 60, img: "data:image/jpeg;base64," },
        { id: 6, nombre: "Licuadora", precio: 15, img: "https://th.bing.com/th/id/OIP.vS95CTbN36QE42_ov_GyMAHaHa?w=210&h=210&c=7&r=0&o=5&dpr=1.3&pid=1.7" },
        { id: 7, nombre: "Cafetera", precio: 25, img: "https://th.bing.com/th/id/OIP.dg84rzpbZCXoqmBk7h72XQHaJt?w=148&h=194&c=7&r=0&o=5&dpr=1.3&pid=1.7" },
        { id: 8, nombre: "Aspiradora", precio: 35, img: "https://th.bing.com/th/id/OIP.26kGhqpf2zVRMZsDYpf2ZQHaHa?w=176&h=180&c=7&r=0&o=5&dpr=1.3&pid=1.7" }
    ];

    let carrito = [];

    function agregarAlCarrito(id) {
        const producto = productos.find(p => p.id === id);
        carrito = [...carrito, producto];
    }

    function eliminarDelCarrito(index) {
        carrito = carrito.filter((_, i) => i !== index);
    }

    function generarFactura() {
        if (carrito.length === 0) {
            alert("El carrito está vacío");
            return;
        }

        const doc = new jsPDF();
        doc.text("Factura de Alquiler", 10, 10);
        let y = 20;
        let total = 0;

        carrito.forEach(p => {
            doc.text(`${p.nombre} - $${p.precio}`, 10, y);
            total += p.precio;
            y += 10;
        });

        doc.text(`Total a pagar: $${total}`, 10, y + 10);
        doc.save("Factura_Alquiler.pdf");
    }
</script>

<style>
    body {
        font-family: 'Arial', sans-serif;
        background-color: #f8f9fa;
        margin: 0;
        padding: 0;
        text-align: center;
    }
    .container {
        max-width: 1200px;
        margin: auto;
        padding: 20px;
    }
    .header {
        background: #007bff;
        color: white;
        padding: 15px;
        font-size: 24px;
        border-radius: 10px;
    }
    .productos {
        display: flex;
        flex-wrap: nowrap;
        overflow-x: auto;
        padding: 10px;
    }
    .producto {
        background: white;
        padding: 15px;
        margin: 10px;
        border-radius: 10px;
        box-shadow: 0px 4px 6px rgba(0,0,0,0.1);
        width: 220px;
        text-align: center;
        flex: 0 0 auto;
    }
    .producto img {
        width: 150px;
        height: 150px;
        object-fit: cover;
    }
    .btn {
        padding: 10px;
        background: #28a745;
        color: white;
        border: none;
        border-radius: 5px;
        cursor: pointer;
        margin-top: 10px;
    }
    .btn:hover {
        background: #218838;
    }
    .carrito {
        margin-top: 30px;
        background: white;
        padding: 15px;
        border-radius: 10px;
        box-shadow: 0px 4px 6px rgba(0,0,0,0.1);
        text-align: left;
    }
    .carrito p {
        display: flex;
        justify-content: space-between;
        align-items: center;
    }
    .btn-eliminar {
        background: #dc3545;
        padding: 5px;
        font-size: 12px;
        cursor: pointer;
    }
</style>

<div class="container">
    <div class="header">Alquiler de Electrodomésticos</div>

    <h2>Productos Disponibles</h2>
    <div class="productos">
        {#each productos as producto}
            <div class="producto">
                <img src="{producto.img}" alt="{producto.nombre}">
                <h3>{producto.nombre}</h3>
                <p>Precio: ${producto.precio}/día</p>
                <button class="btn" on:click={() => agregarAlCarrito(producto.id)}>➕ Agregar</button>
            </div>
        {/each}
    </div>

    <h2>🛒 Carrito de Alquiler</h2>
    <div class="carrito">
        {#if carrito.length === 0}
            <p>Tu carrito está vacío</p>
        {:else}
            {#each carrito as producto, index}
                <p>{producto.nombre} - ${producto.precio} <button class="btn-eliminar" on:click={() => eliminarDelCarrito(index)}>❌</button></p>
            {/each}
        {/if}
    </div>

    <button class="btn" on:click={generarFactura}>🧾 Generar Factura</button>
</div>